import React, { useState }  from 'react'
import "./Tweetbox.css";
import { Avatar, Button } from "@mui/material";
import AddPhotoAlternateOutlinedIcon from "@mui/icons-material/AddPhotoAlternateOutlined";
import axios from "axios";
import { useUserAuth } from "../../../context/UserAuthContext";
import useLoggedinuser from "../../../hooks/useLoggedinuser";

const Tweetbox=()=>{
    const [post, setpost] = useState("");
    const [imageurl, setimageurl] = useState("");
    const [isloading, setisloading] = useState(false);
    const [name, setname] = useState("");
    const [username, setusername] = useState("");
    const { user } = useUserAuth();
    const [loggedinsuer] = useLoggedinuser();
    const email = user?.email;
    const userprofilepic = loggedinsuer[0]?.profileImage
      ? loggedinsuer[0].profileImage
      : user && user.photoURL;

      const handleuploadimage = (e) => {
        setisloading(true);
        const image = e.target.files[0];
        //console.log(image)
        const formData = new FormData();
        formData.set("image", image);
        axios.post("https://api.imgbb.com/1/upload?key=9e63db8a1ef88bd6701fa158975a682e ", formData)
        .then((res) => {
          setimageurl(res.data.data.display_url);
          console.log(res.data.data.display_url);
          setisloading(false);
        })
        .catch((e) => {
          console.log(e);
        });
      };
      const handletweet = (e) => {
        e.preventDefault();
        if (user?.providerData[0]?.providerId === "password") {
          fetch(`https://twiller-twitterclone-ewhk.onrender.com/loggedinuser?email=${email}`)
            .then((res) => res.json())
            .then((data) => {
              //console.log(data[0].name);
              setname(data[0]?.name);
              setusername(data[0]?.username);
            });
        } else {
          setname(user?.displayName);
          setusername(email?.split("@")[0]);
        }
        //console.log(name);
        if (name) {
          const userpost = {
            profilephoto: userprofilepic,
            post: post,
            photo: imageurl,
            username: username,
            name: name,
            email: email,
          };
          //console.log(userpost);
          setpost("");
          setimageurl("");
          fetch("https://twiller-twitterclone-ewhk.onrender.com/post", {
            method: "POST",
            headers: {
              "content-type": "application/json",
            },
            body: JSON.stringify(userpost),
          })
            .then((res) => res.json())
            .then((data) => {
              console.log(data);
            });
        }
      };
    return (
        <div className="tweetBox">
      <form onSubmit={handletweet}>
        <div className="tweetBox__input">
          <Avatar
            src={
              loggedinsuer[0]?.profileImage
                ? loggedinsuer[0].profileImage
                : user && user.photoURL
            }
          />
          <input
            type="text"
            placeholder="What's happening?"
            onChange={(e) => setpost(e.target.value)}
            value={post}
            required
          />
          </div>
        <div className="imageIcon_tweetButton">
          <label htmlFor="image" className="imageIcon">
            {
              isloading ?<p>Uploading Image</p>:<p>{imageurl ? "Image Uploaded":<AddPhotoAlternateOutlinedIcon />}</p>
            }
          </label>
          <input
            type="file"
            id="image"
            className="imageInput"
            onChange={handleuploadimage}
          />
          <Button className="tweetBox__tweetButton" type="submit">
            Tweet
          </Button>
        </div>
      </form>
    </div>
    );
};
export default Tweetbox;
