import React, { useState, useEffect } from "react";
import Post from "../Posts/posts";
import { useNavigate } from "react-router-dom";
import "./Mainprofile.css";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import CenterFocusWeakIcon from "@mui/icons-material/CenterFocusWeak";
import LockResetIcon from "@mui/icons-material/LockReset";
import MyLocationIcon from "@mui/icons-material/MyLocation";
import AddLinkIcon from "@mui/icons-material/AddLink";
import Editprofile from "../Editprofile/Editprofile";
import axios from "axios";
import useLoggedinuser from "../../../hooks/useLoggedinuser";

const Mainprofile = ({ user }) => {
  const navigate = useNavigate();
  const [isloading, setisloading] = useState(false);
  //const loggedinuser = []
  const [loggedinuser] = useLoggedinuser();
  const username = user?.email?.split("@")[0];
  const [post, setpost] = useState([]);
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState("");
  const [loadingLocation, setLoadingLocation] = useState(false);

  useEffect(() => {
    fetch(
      `https://twiller-twitterclone-ewhk.onrender.com/userpost?email=${user?.email}`
    )
      .then((res) => res.json())
      .then((data) => {
        setpost(data);
      });
  }, [user.email]);

  const fetchLocationAndWeather = () => {
    if (navigator.geolocation) {
      setLoadingLocation(true);

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const lat = position.coords.latitude;
          const lng = position.coords.longitude;

          fetch(
            `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
          )
            .then((response) => response.json())
            .then((data) => {
              if (data.results && data.results.length > 0) {
                const addressComponents = data.results[0].address_components;
                const city = addressComponents.find((component) =>
                  component.types.includes("locality")
                )?.long_name;
                const state = addressComponents.find((component) =>
                  component.types.includes("administrative_area_level_1")
                )?.long_name;
                const country = addressComponents.find((component) =>
                  component.types.includes("country")
                )?.long_name;
                const address = `${city}, ${state}, ${country}`;

                setLocation(address);

                fetch(
                  `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${process.env.REACT_APP_WEATHER_API_KEY}`
                )
                  .then((response) => response.json())
                  .then((weatherData) => {
                    if (weatherData.weather && weatherData.weather.length > 0) {
                      const weatherDescription =
                        weatherData.weather[0].description;
                      setWeather(weatherDescription);
                    } else {
                      console.error("No weather data found.");
                    }
                    setLoadingLocation(false);
                  })
                  .catch((error) => {
                    console.error("Error fetching weather data:", error);
                    setLoadingLocation(false);
                  });
              } else {
                console.error("No results found for the given coordinates.");
                setLocation("Location not found");
                setLoadingLocation(false);
              }
            })
            .catch((error) => {
              console.error("Error fetching geocoding data:", error);
              setLoadingLocation(false);
            });
        },
        (error) => {
          console.error("Error obtaining location:", error);
          setLoadingLocation(false);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  };

  const handleuploadcoverimage = (e) => {
    setisloading(true);
    const image = e.target.files[0];
    // console.log(image)
    const formData = new FormData();
    formData.set("image", image);
    axios
      .post(
        "https://api.imgbb.com/1/upload?key=131172eab64930e16173f6f8467ba9a6",
        formData
      )
      .then((res) => {
        const url = res.data.data.display_url;
        // console.log(res.data.data.display_url);
        const usercoverimage = {
          email: user?.email,
          coverimage: url,
        };
        setisloading(false);
        if (url) {
          fetch(
            `https://twiller-twitterclone-ewhk.onrender.com/userupdate/${user?.email}`,
            {
              method: "PATCH",
              headers: {
                "content-type": "application/json",
              },
              body: JSON.stringify(usercoverimage),
            }
          )
            .then((res) => res.json())
            .then((data) => {
              console.log("done", data);
            });
        }
      })
      .catch((e) => {
        console.log(e);
        window.alert(e);
        setisloading(false);
      });
  };
  const handleuploadprofileimage = (e) => {
    setisloading(true);
    const image = e.target.files[0];
    // console.log(image)
    const formData = new FormData();
    formData.set("image", image);
    axios
      .post(
        "https://api.imgbb.com/1/upload?key=131172eab64930e16173f6f8467ba9a6",
        formData
      )
      .then((res) => {
        const url = res.data.data.display_url;
        // console.log(res.data.data.display_url);
        const userprofileimage = {
          email: user?.email,
          profileImage: url,
        };
        setisloading(false);
        if (url) {
          fetch(
            `https://twiller-twitterclone-ewhk.onrender.com/userupdate/${user?.email}`,
            {
              method: "PATCH",
              headers: {
                "content-type": "application/json",
              },
              body: JSON.stringify(userprofileimage),
            }
          )
            .then((res) => res.json())
            .then((data) => {
              console.log("done", data);
            });
        }
      })
      .catch((e) => {
        console.log(e);
        window.alert(e);
        setisloading(false);
      });
  };
  // const data = [
  //     {
  //       _id: "1",
  //       name: "Jane Doe",
  //       username: "jane_doe",
  //       profilePhoto: "https://example.com/profiles/jane.jpg",
  //       post: "Exploring the new features in JavaScript! ðŸš€ #coding #JavaScript",
  //       photo: "https://example.com/posts/javascript.png",
  //     },
  //     {
  //       _id: "2",
